import styled from 'styled-components';

export const Container = styled.div`
  .containerHome{
    height: 98vh;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    background-color: #3d6392; 
  }

  .input{
    width: 60%;
	height: 20px;
	background: #e0dede;
	justify-content: center;
	display: flex;
	margin: 20px auto;
	padding: 10px;
	border: none;
	outline: none;
	border-radius: 5px;
  }

  .btn{
    margin-top: 1rem;
    padding: 10px 40px;
    background: #000;
    color: #FFF;
  }

  .form-login{
    width: 350px;
	height: 500px;
	background: #FFF;
	overflow: hidden;
	border-radius: 10px;
	box-shadow: 5px 20px 50px #000;
  }

  .signup{
	position: relative;
	width:100%;
	height: 100%;
}

`;
