import styled from 'styled-components';

export const Container = styled.div`
  .containerHome{
    height: 98vh;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    background-color: #3d6392; 
  }

  .input{
    padding: 10px;
    width: 300px;
    max-width: 90%;
    font-size: 1rem;
    letter-spacing: 1px;
  }

  .btn{
    margin-top: 1rem;
    padding: 10px 40px;
    background: #000;
    color: #FFF;
  }

`;
