import React, { useState,useEffect } from 'react';
import { useSelector } from 'react-redux/es/exports';
import './UserCard.scss';
import placeholderApi from '../../common/apis/placeholderApi';
import {useDispatch} from 'react-redux';
import {addPosts,getAllPosts} from '../../features/users/usersSlice';
import UserDetail from '../UserDetail/UserDetail';

const UserCard = (props) => {
    const data = props.data;
    const [open, setOpen] = useState(false);
    const sidebar = "sidebar";
    const sidebarOpen= "sidebarOpen";
    const dispatch = useDispatch();
    useEffect(()=>{
        const fetchUsersPosts = async () => {
            const response = await placeholderApi.get(`posts?userId=${data.id}`)
            .catch((error)=>{
                console.log(error);
            });
            console.log("The Response from the API placeholder is: ",response);
            dispatch(addPosts(response.data));
        }
        fetchUsersPosts();
    },[]);
    const posts = useSelector(getAllPosts);
    //let renderUsers = "";
    console.log('Response state: ' ,posts);
    return (
        <>
        <div className="card-item" 
        onClick={()=> setOpen(!open)}
        >
            <div className="card-inner">
                <div className="card-top">
                    <img src={data.avatar} alt={data.first_name}></img>
                </div>
                <div className="card-bottom">
                    <div className="card-info">
                        <h4> {data.first_name+" "+data.last_name} </h4>
                        <p> {data.email} </p>
                    </div>
                </div>
            </div>
        </div>
        <div className={open ? sidebarOpen : sidebar}>
        <div className="card-innerpost">
                <div className="card-top">
                    <img src={data.avatar} alt={data.first_name}></img>
                </div>
                <div className="card-bottom">
                    <div className="card-info">
                        <h4> {data.first_name+" "+data.last_name} </h4>
                        <p> {data.email} </p>
                    </div>
                    <UserDetail/>
                </div>
            </div>
        </div>
        </>
    );
};

export default UserCard;
