import styled from 'styled-components';
export const Container = styled.div`
    .card-item{
        max-width: 340px;
        margin: auto;
        overflow-y: auto;
        position: relative;
        z-index: 1;
        overflow-x: hidden;
        background-color: rgba(255, 255, 255, 1);
        display: grid;
        transition: 0.3s;
        flex-direction: column;
        border-radius: 10px;
        box-shadow: 0 0 0 8px rgba(255, 255, 255, 0.2);
    }

    .card-cover{
        background-image: '../../images/bckDiff.png',
    }
`;
