import React  from 'react';
import {useForm} from 'react-hook-form';
import userApi from '../../common/apis/userApi';
import {Link} from 'react-router-dom';
import './Home.scss';

const Home = () => {
    const { register, handleSubmit, watch, formState: { errors } } = useForm();
    const onSubmit = async (data) => {
        const email_data= data.email;
        const password_data = data.password;
        let items= {email:email_data,password:password_data};
        let response=  await userApi.post(`login`,JSON.stringify(items))
        .catch((error)=>{
            console.log(error);
        });
        console.log("The Response from the API: ",response);
        


    }
    return (
        <div className="containerHome">
            <form onSubmit={handleSubmit(onSubmit)}>
                <div>
                    <p>
                        <label className='label'>Correo</label>
                    </p>
                    <p>
                        <input name="email" className='input' 
                        {...register("email", { required: true })}                        
                        />
                        
                    </p>
                    {errors.email && <span className="text-danger text-small">El correo es obligatorio</span>}
                </div>
                <div>
                    <p>
                        <label className='password'>Contraseña</label>
                    </p>
                    <p>
                        <input name="password" className='input' type='password' 
                        {...register("password", { required: true })}
                        />
                        
                    </p>
                    {errors.password && <span className="text-danger text-small">La contraseña es obligatoria</span>}
                </div>
                <button className="btn">Login</button>
            </form>
        </div>
    );
};

export default Home;
