import React,{ useEffect} from 'react'
import {Link} from "react-router-dom";
import {useParams} from "react-router";
import placeholderApi from '../../common/apis/placeholderApi';
import { addPosts } from '../../features/users/usersSlice';
import {useDispatch} from 'react-redux';
const UserDetail = () => {
    const {idUser} = useParams();
    const dispatch = useDispatch();
    useEffect(()=>{
        const fetchUsersPosts = async () => {
            const response = await placeholderApi.get(`posts?userId=${idUser}`)
            .catch((error)=>{
                console.log(error);
            });
            //console.log("The Response from the API placeholder is: ",response);
            dispatch(addPosts(response.data));
        }
        fetchUsersPosts();
    },[]);
  return (
    <>
        <Link to={`/users`}>
        <div>Regresar</div>
        </Link>        
        <div>UserDetail</div>
    </>
  )
}

export default UserDetail
