import React, { useState } from 'react';
import {Link} from "react-router-dom";
import './UserCard.scss';


const UserCard = (props) => {
    const data = props.data;
    const [open, setOpen] = useState(false);
    const sidebar = "sidebar";
    const sidebarOpen= "sidebarOpen";
    return (
        <>
        <div className="card-item" 
        onClick={()=> setOpen(!open)}
        >
            <div className="card-inner">
                <div className="card-top">
                    <img src={data.avatar} alt={data.first_name}></img>
                </div>
                <div className="card-bottom">
                    <div className="card-info">
                        <h4> {data.first_name+" "+data.last_name} </h4>
                        <p> {data.email} </p>
                    </div>
                </div>
            </div>
        </div>
        <div className={open ? sidebarOpen : sidebar}>
        <div className="card-innerpost">
                <div className="card-top">
                    <img src={data.avatar} alt={data.first_name}></img>
                </div>
                <div className="card-bottom">
                    <div className="card-info">
                        <h4> {data.first_name+" "+data.last_name} </h4>
                        <p> {data.email} </p>
                    </div>
                    <Link to={`/user/${data.id}`} ><button className="btn btn-info">Ver Posts</button></Link>
                </div>
            </div>
        </div>
        </>
    );
};

export default UserCard;
