import React from 'react';
import { useSelector } from 'react-redux/es/exports';
import { getAllPosts } from '../../features/users/usersSlice';
import PostsDetail from '../PostsDetail/PostsDetail';

const UserDetail = () => {
    let x =1;
    const posts = useSelector(getAllPosts);
    let renderPosts = "";
    //.log('Response state: ' ,posts);
       // console.log('Response state: de la vuelta: '+x ,posts);
        renderPosts = posts.length > 0 ? (
            //console.log("hay datos")
            posts.map((post, index)=>{
               //  console.log("array mapeado: ",post);
               if(x===1){
                return <PostsDetail key={index} data={post} />
               }else if(x===6){
                x=1;
            }
            x++;                
            })
        ) : (<div className="users-error"><h3> Error </h3></div>);

    return (
       [renderPosts] 
    );
};

export default UserDetail;
