import styled from 'styled-components';
export const Container = styled.body`
    body{
        background-image: linear-gradient(180deg, #5aa1ef 0, #3d6392 50%, #1e2b3f 100%) ;
        background-repeat: no-repeat;
        background-size: 100% 100%;
    }
    
`;
