import styled from 'styled-components';
export const Container = styled.div`
    .post-title{
        color: #FFF;
    }

    .post-body{
        color: #FFF;
    }

    .card {
        margin: 10px;
        min-width: 200px;
        width: 300px;
        height: 200px;
        position: relative;
        perspective: 1000px;
    }
`;
